#include &lt;iostream&gt;

class Node
{
public:
    int data;
    Node *next;

    explicit Node(int data): data(data), next(nullptr)
    {
    }

    Node(int data, Node *next): data(data), next(nullptr)
    {
    }
};

class singleLinkedList
{
public:
    Node *head;

    singleLinkedList(): head(nullptr)
    {
    }

    void insertAtHead(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            auto *newNode = new Node(data);
            newNode-&gt;next = head;
            head = newNode;

        }
    }

    void insertAtTail(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node *temp = head;
            while (temp-&gt;next != nullptr)
            {
                temp = temp-&gt;next;
            }

            temp-&gt;next = new Node(data);
        }
    }

    void insertAtanyPosition(int data, int position)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            int currentPos = 1;
            Node *temp = head;
            Node *previous = nullptr;
            while (temp != nullptr && currentPos &lt; position)
            {

                currentPos++;
                previous = temp;
                temp = temp-&gt;next;
            }

            if (currentPos == position)
            {
                Node *newNode = new Node(data);
                newNode-&gt;next = temp;
                previous-&gt;next = newNode;
            }
        }
    }

    void deleteFromPosition(int position)
    {
        int currentPos = 1;
        Node *temp = head;
        Node *previous = nullptr;
        while (temp != nullptr && currentPos &lt; position)
        {
            currentPos++;
            previous = temp;
            temp = temp-&gt;next;
        }

        if (currentPos == position)
        {
            previous-&gt;next = temp-&gt;next;
        }
    }

    void display() const
    {
        if (head == nullptr)
        {
            std::cout &lt;&lt; "List is empty" &lt;&lt; std::endl;
        }
        else
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";
                temp = temp-&gt;next;
            }
            std::cout &lt;&lt; std::endl;
        }

    }

    ~singleLinkedList()
    {
        if (head != nullptr)
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                const Node *nextNode = temp-&gt;next;
                delete temp;
                temp = nextNode;
            }

        }
    }
};

int main()
{
    singleLinkedList obj;
    obj.insertAtHead(12);
    obj.insertAtTail(24);
    obj.insertAtTail(36);
    obj.insertAtTail(48);
    obj.insertAtTail(94);
    std::cout &lt;&lt; "list before modification" &lt;&lt; std::endl;
    obj.display();
    std::cout &lt;&lt; "list after modification" &lt;&lt; std::endl;
    obj.insertAtanyPosition(72, 5);
    obj.display();
    std::cout &lt;&lt; "list after deleting" &lt;&lt; std::endl;
    obj.deleteFromPosition(4);
    obj.display();
    return 0;
}
Output :
list before modification
12 24 36 48 94 
list after modification
12 24 36 48 72 94 
list after deleting
12 24 36 72 94

