#include &lt;iostream&gt;

class Node
{
public:
    int data;
    Node *next;
    Node *prev;

    explicit Node(int data): data(data), next(nullptr),prev(nullptr){}

    Node(int data, Node *next,Node* prev ): data(data), next(nullptr), prev(prev){}
};

class doubleLinkedList
{
public:
    Node *head;

    doubleLinkedList(): head(nullptr){}

    void insertAtHead(int data)
    {
        if(head==nullptr)
        {
            head = new Node(data);
        }
        else
        {
            auto *newNode = new Node(data);
            head-&gt;prev = newNode;
            newNode-&gt;next = head;
            head = newNode;
            head-&gt;prev = nullptr;

        }
    }

    void insertAtTail(int data)
    {
        if(head==nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node * temp=head;
            while(temp-&gt;next!=nullptr)
            {
                temp=temp-&gt;next;
            }
            Node* newNode = new Node(data);
            newNode-&gt;prev=temp;
            temp-&gt;next=newNode;
        }
    }

    void insertAtanyPosition(int data,int position)
    {
        if(head==nullptr)
        {
            head = new Node(data);
        }
        else
        {
            int currentPos=1;
            Node * temp=head;
            Node * previous=nullptr;
            while(temp!=nullptr && currentPos&lt;position)
            {
                currentPos++;
                temp=temp-&gt;next;
            }

            if(currentPos==position)
            {
                Node * newNode = new Node(data);
                newNode-&gt;next=temp;
                temp-&gt;prev-&gt;next=newNode;
                newNode-&gt;prev=temp-&gt;prev;
                temp-&gt;prev=newNode;
            }
        }
    }

    void deleteFromPosition(int position)
    {
            int currentPos=1;
            Node * temp=head;
            while(temp!=nullptr && currentPos&lt;position)
            {
                currentPos++;
                temp=temp-&gt;next;
            }

            if(currentPos==position)
            {
                temp-&gt;prev-&gt;next=temp-&gt;next;
                temp-&gt;next-&gt;prev=temp-&gt;prev;
            }
    }
    void display() const
    {
        if(head==nullptr)
        {
            std::cout&lt;&lt;"List is empty"&lt;&lt;std::endl;
        }
        else
        {
            const Node * temp=head;
            while(temp!=nullptr)
            {
                std::cout&lt;&lt;temp-&gt;data&lt;&lt;" ";
                temp=temp-&gt;next;
            }
            std::cout&lt;&lt;std::endl;
        }

    }

    ~doubleLinkedList()
    {
        if(head!=nullptr)
        {
           const Node * temp=head;
            while(temp!=nullptr)
            {
                const Node * nextNode=temp-&gt;next;
                delete temp;
                temp=nextNode;
            }

        }
    }
};

int main()
{
    doubleLinkedList obj;
    obj.insertAtHead(12);
    obj.insertAtTail(24);
    obj.insertAtHead(6);
    obj.insertAtTail(36);
    obj.insertAtTail(48);
