3 Reverse single linked list
#include &lt;iostream&gt;
#include&lt;stack&gt;

class Node
{
public:
    int data;
    Node *next;

    explicit Node(int data): data(data), next(nullptr)
    {
    }

    Node(int data, Node *next): data(data), next(nullptr)
    {
    }
};

class singleLinkedList
{
public:
    Node *head;

    singleLinkedList(): head(nullptr)
    {
    }

    void insertAtTail(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node *temp = head;
            while (temp-&gt;next != nullptr)
            {
                temp = temp-&gt;next;
            }
            temp-&gt;next = new Node(data);
        }
    }

    void reverseList()
    {
        Node *prev = nullptr;
        Node *curr = head;
        Node *next = head-&gt;next;
        while (next != nullptr)
        {
            curr-&gt;next = prev;
            prev = curr;
            curr = next;
            next = next-&gt;next;
        }
        curr-&gt;next = prev;
        head = curr;
    }

    Node * reverseLeftToRight(Node * &headNode,int left,int right)
    {
        if (left == right) return headNode; // No need to reverse if left == right

        Node* dummy = new Node(0);  // Dummy node to handle edge cases
        dummy-&gt;next = headNode;
        Node* prev = dummy;

        // Move `prev` to the node just before `left`
        for (int i = 1; i &lt; left; i++) {
            prev = prev-&gt;next;
        }

        Node* start = prev-&gt;next;  // The first node in the section to be reversed
        Node* temp = start;
        std::stack&lt;Node*&gt; stk;

        // Push nodes to be reversed onto the stack
        for (int i = left; i &lt;= right; i++) {
            stk.push(temp);
            temp = temp-&gt;next;
        }

        // Pop from the stack and reverse the pointers
        Node* nextNode = temp; // The node right after the section to be reversed
        while (!stk.empty()) {
            prev-&gt;next = stk.top();
            stk.pop();
            prev = prev-&gt;next;
        }

        // Connect the reversed part to the rest of the list
        prev-&gt;next = nextNode;

        return dummy-&gt;next;
    }
    void display(Node* head) const
    {
        if (head == nullptr)
        {
            std::cout &lt;&lt; "List is empty" &lt;&lt; std::endl;
        }
        else
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";
                temp = temp-&gt;next;
            }
            std::cout &lt;&lt; std::endl;
        }
    }

    ~singleLinkedList()
    {
        if (head != nullptr)
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                const Node *nextNode = temp-&gt;next;
                delete temp;
                temp = nextNode;
            }
        }
    }
};

int main()
{
    singleLinkedList obj;
    obj.insertAtTail(24);
    obj.insertAtTail(36);
    obj.insertAtTail(48);
    obj.insertAtTail(72);
    std::cout &lt;&lt; "list before reversal" &lt;&lt; std::endl;
    obj.display(obj.head);
    obj.reverseList();
    std::cout &lt;&lt; "reversed list" &lt;&lt; std::endl;
    obj.display(obj.head);
    std::cout &lt;&lt;"reversed left to right"&lt;&lt; std::endl;
    Node* node=obj.reverseLeftToRight(obj.head,1,2);
    obj.display(node);
    return 0;
}
Output:
list before reversal
24 36 48 72 
reversed list
72 48 36 24 
reversed left to right
48 72 36 24

