#include &lt;iostream&gt;

class Node
{
public:
    int data;
    Node *next;
    Node *prev;

    explicit Node(int data): data(data), next(nullptr), prev(nullptr)
    {
    }

    Node(int data, Node *next, Node *prev): data(data), next(nullptr), prev(prev)
    {
    }
};

class doubleLinkedList
{
public:
    Node *head;

    doubleLinkedList(): head(nullptr)
    {
    }

    void insertAtTail(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node *temp = head;
            while (temp-&gt;next != nullptr)
            {
                temp = temp-&gt;next;
            }
            Node *newNode = new Node(data);
            newNode-&gt;prev = temp;
            temp-&gt;next = newNode;
        }
    }

    void reverseDoubleList()
    {
        if (head == nullptr)
            return;
        Node *curr = head;
        Node *temp = nullptr;
        while (curr != nullptr)
        {
            temp = curr-&gt;prev;
            curr-&gt;prev = curr-&gt;next;
            curr-&gt;next = temp;
            curr = curr-&gt;prev;
        }
        if (temp != nullptr)
        {
            head = temp-&gt;prev;
        }
    }

    void display() const
    {
        if (head == nullptr)
