#include &lt;iostream&gt;

class Node
{
public:
    int data;
    Node *next;

    explicit Node(int data): data(data), next(nullptr)
    {
    }

    Node(int data, Node *next): data(data), next(nullptr)
    {
    }
};

class singleLinkedList
{
public:
    Node *head;

    singleLinkedList(): head(nullptr)
    {
    }

    void insertAtHead(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            auto *newNode = new Node(data);
            newNode-&gt;next = head;
            head = newNode;

        }
    }

    void insertAtTail(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node *temp = head;
            while (temp-&gt;next != nullptr)
            {
                temp = temp-&gt;next;
            }

            temp-&gt;next = new Node(data);
        }
    }

    Node *rotateKtimes(Node *head, int k)
    {
        if (head == nullptr || k == 0 || head-&gt;next == nullptr)
            return head;

        int length = 1;
        Node *tail = head;
        while (tail-&gt;next != nullptr)
        {
            tail = tail-&gt;next;
            length++;
        }

        k = k % length;
        if (k == 0)
            return head;

        tail-&gt;next = head;

        Node *newTail = head;
        for (int i = 1; i &lt; length - k; ++i)
        {
            newTail = newTail-&gt;next;
        }

        Node *newHead = newTail-&gt;next;
        newTail-&gt;next = nullptr;

        return newHead;
    }

    void display(Node *head) const
    {
        if (head == nullptr)
        {
            std::cout &lt;&lt; "List is empty" &lt;&lt; std::endl;
        }
        else
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";
                temp = temp-&gt;next;
            }
            std::cout &lt;&lt; std::endl;
        }

    }

    ~singleLinkedList()
    {
        if (head != nullptr)
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                const Node *nextNode = temp-&gt;next;
                delete temp;
                temp = nextNode;
            }

        }
    }
};

int main()
{
    singleLinkedList obj;
    obj.insertAtHead(12);
    obj.insertAtTail(24);
    obj.insertAtTail(36);
    obj.insertAtTail(48);
    obj.insertAtTail(94);
    std::cout &lt;&lt; "list before modification" &lt;&lt; std::endl;
    obj.display(obj.head);
    Node *node = obj.rotateKtimes(obj.head, 2);
    std::cout &lt;&lt; "rotated List" &lt;&lt; std::endl;
    obj.display(node);
    return 0;
}
