    std::cout &lt;&lt; "Binary Search tree" &lt;&lt; std::endl;
    obj.printTree(obj.root);

    auto node = obj.deleteNode(obj.root, 6);
    std::cout &lt;&lt; "Binary Search tree after Delete " &lt;&lt; std::endl;
