class Node
{
    public:
    int data;
    Node* next;
    Node(int data):data(data), next(nullptr) {};
};

class Stack
{
    public:
    Node* head;
    Stack():head(nullptr) {}
    void push(int data)
    {
        Node* newNode = new (std::nothrow) Node(data);
        if (newNode == nullptr) {
            printf("Stack is full");
            return;
        }
        if (head == nullptr) {
            head = newNode;
        } else {
            newNode-&gt;next = head;
            head = newNode;
        }
    }

    int pop()
    {  
        if (head == nullptr) {
            printf("Stack is empty");
            return -1;
        }     
        Node * tempNode = head;
        int temp = tempNode-&gt;data;
        head = head-&gt;next;
        delete tempNode;
        return temp;
    }
    ~Stack(){
        Node * temp = head;
        while (temp != nullptr) {
            Node* deleteNode = temp;
            temp = temp-&gt;next;
            delete deleteNode;
        }
    }

    void display() {
        Node* temp = head;
        while(temp != nullptr) {
            printf("\n data = %d",temp-&gt;data);
            temp = temp-&gt;next;
        }
    }

};

class Queue 
{
    public:
    Stack stack1;
    Stack stack2;

    void enqueue(int data) {
        stack1.push(data);
    }

    int dequeue() {
        if (stack2.head == nullptr) {
            while (stack1.head != nullptr) {
                int data = stack1.pop();
                stack2.push(data);
            }
        }
        if (stack2.head == nullptr) {
            printf("Queue is empty");
            return -1;
        }
        return stack2.pop();
    }

    bool isEmpty() {
        return (stack1.head == nullptr && stack2.head == nullptr);
    }

    void display() {       
        stack2.display();       
        stack1.display();        
    }
};

