void bubbleSort(std::vector&lt;int&gt; &v)
{
    for (int i = 0; i &lt; v.size(); i++)
    {
        for (int j = 0; j &lt; v.size() - 1 - i; j++)
        {
            if (v[j] &gt; v[j + 1])
            {
                std::swap(v[j], v[j + 1]);
            }
        }
    }
}

int main()
{
    std::vector&lt;int&gt; v{5, 8, 4, 3, 2, 1};
    bubbleSort(v);
    std::cout &lt;&lt; "Sorted elements with Bubble sort: ";
    for (auto x : v)
        std::cout &lt;&lt; x &lt;&lt; " ";
    return 0;
}
