void insertionSort(std::vector&lt;int&gt; &v)
{
    for (int i = 1; i &lt; v.size(); i++)
    {
        const int x = v[i];
        int j = i - 1;
        while (j &gt;= 0 && v[j] &gt; x)
        {
            v[j + 1] = v[j];
            j--;
        }
        v[j + 1] = x;
    }
}

int main()
{
    std::vector&lt;int&gt; v{5, 8, 4, 3, 2, 1};
    insertionSort(v);
    std::cout &lt;&lt; "Sorted elements with Insertion sort: ";
    for (auto x : v)
        std::cout &lt;&lt; x &lt;&lt; " ";
    return 0;
}
