void selectionSort(std::vector&lt;int&gt; &v)
{
    for (int i = 0; i &lt; v.size(); i++)
    {
        int minIndex = i;
        for (int j = i + 1; j &lt; v.size(); j++)
        {
            if (v[j] &lt; v[minIndex])
            {
                minIndex = j;
            }
        }
        std::swap(v[i], v[minIndex]);
    }
}

int main()
{
    std::vector&lt;int&gt; v{5, 8, 4, 3, 2, 1};
    selectionSort(v);
    std::cout &lt;&lt; "Sorted elements with Selection sort: ";
    for (auto x : v)
        std::cout &lt;&lt; x &lt;&lt; " ";
    return 0;
}

