#include &lt;iostream&gt;
#include &lt;new&gt;
#include &lt;queue&gt;

class Node
{
    public:
    int data;
    Node* leftChild;
    Node* rightChild;
    Node* parent;
    Node(int data):data(data), leftChild(nullptr), rightChild(nullptr), parent(nullptr) {};
};

class MaxHeap
{
    public:
    Node* head;
    MaxHeap():head(nullptr){}
    void insertData(int data)
    {
        Node* newNode = new (std::nothrow) Node(data);
        if (newNode == nullptr) {
            return;
        }
        if (head == nullptr) {
            head = newNode;
        } else {
            std::queue&lt;Node*&gt; q;
            q.push(head);
            while (!q.empty()) {
                Node* cur = q.front(); q.pop();

                if (cur-&gt;leftChild == nullptr) {
                    cur-&gt;leftChild = newNode;
                    newNode-&gt;parent = cur;
                    break;
                } else {
                    q.push(cur-&gt;leftChild);
                }

                if (cur-&gt;rightChild == nullptr) {
                    cur-&gt;rightChild = newNode;
                    newNode-&gt;parent = cur;
                    break;
                } else {
                    q.push(cur-&gt;rightChild);
                }
            }

            // bubble-up to maintain max-heap (swap data values)
            Node* child = newNode;
            while (child-&gt;parent != nullptr && child-&gt;parent-&gt;data &lt; child-&gt;data) {
                std::swap(child-&gt;parent-&gt;data, child-&gt;data);
                child = child-&gt;parent;
            }
        }  
    }

    ~MaxHeap(){      
    }
    void display() {
        if (head == nullptr) {
            std::cout &lt;&lt; "(empty)\n";
            return;
        }
        std::queue&lt;Node*&gt; q;
        q.push(head);
        while (!q.empty()) {
            Node* cur = q.front(); q.pop();
            std::cout &lt;&lt; cur-&gt;data &lt;&lt; " ";
            if (cur-&gt;leftChild) q.push(cur-&gt;leftChild);
            if (cur-&gt;rightChild) q.push(cur-&gt;rightChild);
        }
        std::cout &lt;&lt; std::endl;
    }    
};

int main()
{
    MaxHeap obj;
    obj.insertData(30);
    obj.insertData(15);
    obj.insertData(20);
    obj.insertData(10);
    obj.insertData(12);
    obj.insertData(6);
    obj.insertData(5);
    obj.insertData(40);

    obj.display();
}

