#include&lt;iostream&gt;
#include&lt;vector&gt;

void insertInHeap(std::vector&lt;int&gt; &A, int size)
{
    int i = size;
    int temp = A[i];
    while (i &gt; 0 && temp &gt; A[(i-1)/2]) {
        A[i] = A[(i-1)/2];
        i = (i-1)/2;
    }
    A[i] = temp;
}

int deleteFromHeap(std::vector&lt;int&gt; &A, int size)
{
    int i = 0;
    int maxVal = A[0];
    int temp = A[size-1];
    A[0] = temp;
    int heapSize = size - 1;
    int j = 2*i + 1;

    while (j &lt; heapSize) {
        if (j + 1 &lt; heapSize && A[j+1] &gt; A[j]) {
            j = j + 1;
        }
        if (A[i] &lt; A[j]) {
            std::swap(A[i], A[j]);
            i = j;
            j = 2*i + 1;
        } else {
            break;
        }
    }
    return maxVal;
}

void heapSort(std::vector&lt;int&gt; &A, int size)
{
    int i = 0;
    int temp = A[size-1];
    std::swap(A[0], A[size-1]);
    int heapSize = size - 1;
    int j = 2*i + 1;

    while ( j &lt; heapSize ) {
        if ( j + 1 &lt; heapSize && A[j+1] &gt; A[j]) {
            j = j + 1;
        }
        if (A[i] &lt; A[j]) {
            std::swap(A[i], A[j]);
            i = j;
            j = 2*i + 1;
        } else {
            break;
        }
    }

}

int main()
{
    std::vector&lt;int&gt; H{14,15,5,20,30,8,40};
    int size = H.size();
    for (int i = 1; i &lt; size; i++) {
        insertInHeap(H,i);
    } 

    int newValue = 50;                 
    H.push_back(newValue);            
    insertInHeap(H, static_cast&lt;int&gt;(H.size() - 1)); 
    size = static_cast&lt;int&gt;(H.size());

    std::cout &lt;&lt; "Heap before delete: ";
    for (int i = 0; i &lt; size; i++) {
        std::cout &lt;&lt; H[i] &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    int deletedValue = deleteFromHeap(H, size); 
    H.pop_back();                                
    size = static_cast&lt;int&gt;(H.size());         
    std::cout &lt;&lt; "Deleted max: " &lt;&lt; deletedValue &lt;&lt; std::endl;
    std::cout &lt;&lt; "Heap after delete: ";
    for (int i = 0; i &lt; size; i++) {
        std::cout &lt;&lt; H[i] &lt;&lt; " ";
    }

    std::cout &lt;&lt; std::endl;
    for (int i = size; i &gt; 1; i--) {
        heapSort(H,i);
    }
    std::cout &lt;&lt; "After heapsort: ";
    for (int i = 0; i &lt; size; i++) {
        std::cout &lt;&lt; H[i] &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl; 

    return 0;
}
