#include &lt;iostream&gt;
#include &lt;queue&gt;

class Node
{
public:
    int data;
    Node *leftChild;
    Node *rightChild;

    explicit Node(int data) : data(data), leftChild(nullptr), rightChild(nullptr)
    {
    }
};

class binaryTree
{
public:
    Node *root;

    binaryTree() : root(nullptr)
    {
    }

    // Function to insert a node in a binary tree (level-order insertion)
    void insert(int data)
    {
        Node *newNode = new Node(data);

        if (!root)
        {
            root = newNode;
            return;
        }

        std::queue&lt;Node *&gt; q;
        q.push(root);

        while (!q.empty())
        {
            Node *temp = q.front();
            q.pop();

            if (!temp-&gt;leftChild)
            {
                temp-&gt;leftChild = newNode;
                return;
            }
            else
            {
                q.push(temp-&gt;leftChild);
            }

            if (!temp-&gt;rightChild)
            {
                temp-&gt;rightChild = newNode;
                return;
            }
            else
            {
                q.push(temp-&gt;rightChild);
            }
        }
    }

    // Function to perform level-order traversal
    void levelOrder()
    {
        if (!root)
        {
            std::cout &lt;&lt; "Tree is empty." &lt;&lt; std::endl;
            return;
        }

        std::queue&lt;Node *&gt; q;
        q.push(root);

        while (!q.empty())
        {
            Node *temp = q.front();
            q.pop();

            std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";

            if (temp-&gt;leftChild)
            {
                q.push(temp-&gt;leftChild);
            }

            if (temp-&gt;rightChild)
            {
                q.push(temp-&gt;rightChild);
            }
        }

        std::cout &lt;&lt; std::endl;
    }

    // Function to find the deepest node
    Node *findDeepestNode()
    {
        if (!root)
            return nullptr;

        std::queue&lt;Node *&gt; q;
        q.push(root);
        Node *temp = nullptr;

        while (!q.empty())
        {
            temp = q.front();
            q.pop();

            if (temp-&gt;leftChild)
            {
                q.push(temp-&gt;leftChild);
            }

            if (temp-&gt;rightChild)
            {
                q.push(temp-&gt;rightChild);
            }
        }

        return temp; // The last node processed is the deepest node
    }

    // Function to delete the deepest node
    void deleteDeepestNode(Node *deepest)
    {
        if (!root || !deepest)
            return;

        std::queue&lt;Node *&gt; q;
        q.push(root);

        while (!q.empty())
        {
            Node *temp = q.front();
            q.pop();

            if (temp == deepest)
            {
                delete deepest;
                return;
            }

            if (temp-&gt;leftChild)
            {
                if (temp-&gt;leftChild == deepest)
                {
                    delete temp-&gt;leftChild;
                    temp-&gt;leftChild = nullptr;
                    return;
                }
                else
                {
                    q.push(temp-&gt;leftChild);
                }
            }

            if (temp-&gt;rightChild)
            {
                if (temp-&gt;rightChild == deepest)
                {
                    delete temp-&gt;rightChild;
                    temp-&gt;rightChild = nullptr;
                    return;
                }
                else
                {
                    q.push(temp-&gt;rightChild);
                }
            }
        }
    }

    // Function to delete a node with a given value
    void deleteNode(int value)
    {
        if (!root)
        {
            std::cout &lt;&lt; "Tree is empty." &lt;&lt; std::endl;
            return;
        }

        if (root-&gt;data == value && !root-&gt;leftChild && !root-&gt;rightChild)
        {
            // If the tree only has one node and it's the one to delete
            delete root;
            root = nullptr;
            return;
        }

        std::queue&lt;Node *&gt; q;
        q.push(root);
        Node *nodeToDelete = nullptr;
        Node *temp = nullptr;

        // Find the node to delete
        while (!q.empty())
        {
            temp = q.front();
            q.pop();

            if (temp-&gt;data == value)
            {
                nodeToDelete = temp;
            }

            if (temp-&gt;leftChild)
            {
                q.push(temp-&gt;leftChild);
            }

            if (temp-&gt;rightChild)
            {
                q.push(temp-&gt;rightChild);
            }
        }

        if (!nodeToDelete)
        {
            std::cout &lt;&lt; "Node not found in the tree." &lt;&lt; std::endl;
            return;
        }

        // Find the deepest node
        Node *deepest = findDeepestNode();

        // Replace nodeToDelete's data with deepest node's data
        if (deepest)
        {
            nodeToDelete-&gt;data = deepest-&gt;data;

            // Delete the deepest node
            deleteDeepestNode(deepest);
        }
    }
};

int main()
{
    binaryTree tree;

    // Insert nodes into the tree
    tree.insert(1);
    tree.insert(2);
    tree.insert(3);
    tree.insert(4);
    tree.insert(5);
    tree.insert(6);
    tree.insert(7);

    std::cout &lt;&lt; "Level-order traversal before deletion:" &lt;&lt; std::endl;
    tree.levelOrder();

    // Delete a node
    tree.deleteNode(3);

    std::cout &lt;&lt; "Level-order traversal after deleting node 3:" &lt;&lt; std::endl;
    tree.levelOrder();

    return 0;
}
