#include &lt;iostream&gt;
#include&lt;map&gt;

/*LRU Cache :least recently used value/node should be removed  in case the cache is full
* most recently used value/node should be always at the head for easy access*/
class Node
{
public:
    int data;
    Node *next;
    Node *prev;
    int key;

    Node(int key, int data): key(key), data(data), next(nullptr), prev(nullptr)
    {
    }

};

class lruCache
{
public:
    Node *head;
    Node *tail;
    std::unordered_map&lt;int, Node *&gt; cache;
    int capacity;

    explicit lruCache(int capacity): capacity(capacity), head(nullptr), tail(nullptr)
    {
    }

    void insert(Node *node)
    {
        if (head == nullptr)
        {
            head = node;
            tail = node;
        }
        else
        {
            node-&gt;next = head;
            head-&gt;prev = node;
            head = node;
        }
        node-&gt;prev = nullptr;
    }

    void remove(Node *node)
    {
        if (node == nullptr)
            return;

        if (head == node)
        {
            head = head-&gt;next;
            if (head != nullptr)
                head-&gt;prev = nullptr;
        }
        else if (tail == node)
        {
            tail = tail-&gt;prev;
            if (tail != nullptr)
                tail-&gt;next = nullptr;
        }
        else
        {
            if (node-&gt;prev != nullptr)
                node-&gt;prev-&gt;next = node-&gt;next;
            if (node-&gt;next != nullptr)
                node-&gt;next-&gt;prev = node-&gt;prev;
        }
    }

    Node *get(int key)
    {
        if (cache.find(key) == cache.end())
            return nullptr;
        auto node = cache[key];
        remove(node);
        insert(node);
        return node;
    }

    void put(int key, int data)
    {
        if (cache.find(key) != cache.end())
        {
            auto node = cache[key];
            node-&gt;data = data;
            remove(node);
            insert(node);
        }
        else
        {
            if (cache.size() == capacity)
            {
                cache.erase(tail-&gt;key);
                remove(tail);
            }
            Node *newNode = new Node(key, data);
            cache.insert({key, newNode});
            insert(newNode);
        }

    }

    void display() const
    {
        if (head == nullptr)
        {
            std::cout &lt;&lt; "Cache is empty" &lt;&lt; std::endl;
            return;
        }

        const Node *temp = head;
        while (temp != nullptr)
        {
            std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";
            temp = temp-&gt;next;
        }
        std::cout &lt;&lt; std::endl;

    }

    ~lruCache()
    {
        while (head != nullptr)
        {
            Node *nextNode = head-&gt;next;
            delete head;
            head = nextNode;
        }
        cache.clear();
    }
};

int main()
{
    lruCache obj(2);
    obj.put(1, 2);
    obj.put(3, 4);
    std::cout &lt;&lt; "lruCache after put:" &lt;&lt; std::endl;
    obj.display();
    std::cout &lt;&lt; "lruCache after get:" &lt;&lt; std::endl;
    obj.get(1);
    obj.display();
    return 0;
}
