class singleLinkedList
{
public:
    Node *head;

    singleLinkedList(): head(nullptr)
    {
    }

    void insertAtTail(int data)
    {
        if (head == nullptr)
        {
            head = new Node(data);
        }
        else
        {
            Node *temp = head;
            while (temp-&gt;next != nullptr)
            {
                temp = temp-&gt;next;
            }

            temp-&gt;next = new Node(data);
        }
    }

    Node *mergeLists(Node *&list1, Node *&list2)
    {
        if (list1 == nullptr)
            return list2;
        if (list2 == nullptr)
            return list1;

        Node *last = nullptr;
        Node *third = nullptr;
        while (list1 != nullptr && list2 != nullptr)
        {
            if (list1-&gt;data &lt; list2-&gt;data)
            {
                if (third == nullptr)
                {
                    last = third = list1;
                }
                else
                {
                    last-&gt;next = list1;
                    last = last-&gt;next;
                }
                list1 = list1-&gt;next;

            }
            else
            {
                if (third == nullptr)
                {
                    last = third = list2;
                }
                else
                {
                    last-&gt;next = list2;
                    last = last-&gt;next;
                }
                list2 = list2-&gt;next;
            }
        }
        if (list1 != nullptr)
        {
            last-&gt;next = list1;
            list1 = nullptr;
        }
        else if (list2 != nullptr)
        {
            last-&gt;next = list2;
            list2 = nullptr;
        }
        return third;
    }


    void display(Node *head) const
    {
        if (head == nullptr)
        {
            std::cout &lt;&lt; "List is empty" &lt;&lt; std::endl;
        }
        else
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                std::cout &lt;&lt; temp-&gt;data &lt;&lt; " ";
                temp = temp-&gt;next;
            }
            std::cout &lt;&lt; std::endl;
        }

    }

    ~singleLinkedList()
    {
        if (head != nullptr)
        {
            const Node *temp = head;
            while (temp != nullptr)
            {
                const Node *nextNode = temp-&gt;next;
                delete temp; // Frees the current node
                temp = nextNode; // Moves to the next node
            }
        }
    }
};

